package Basics;

public class While_Loop3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1;						
		
		while(i <= 20) {
			if((i % 2) == 0)				//to print even number
			//	if((i % 2) != 0)			// to print odd number
				
			System.out.println(i);
			i++;
		}
		
			
		}
}
	

		
