package Basics;

public class While_Loop6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1;						
	
		for(int j =1; j <=5; j++) {
			while (i <=10) {
				if(j==5)
					break;
				System.out.println(i + "x" + j + "="+ i*j);
				i++;
			}
				i=1;
				System.out.println("_____________________________");
						
			}
		}
		
			
		}

	

		
