package Basics;

public class While_Loop_factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1;
		int num = 5;
		int fact  = 0;
		
		// 1 + 2+ 3+ 4+ 5 = 15
		
		while(i <= num) {
			fact = fact + i;
			i++;
			
			
		System.out.println(" = "+ fact);
	
		}
		
			
		}
}
	

		
